package week1.day1;

public class ConditionalStatements {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// print positive if number is greater than 0

		int num = 5;

		if (num % 2 == 0) {

			System.out.println("Even");

		}

		else {
			System.out.println("odd");
		}

	}

}
